, hycrSeq
, hymmSeq
) VALUES (
0
, #{hycrSeq}
, #{hymmSeq}
)


	SELECT last_insert_id()

INSERT INTO hybdChatMember
(
hycmDelNy
, hycrSeq
, hymmSeq
) VALUES (
0
, #{hycrSeq}
, #{hymmSeq}
)


	SELECT last_insert_id()

mapper.select:
SELECT a.hycrSeq
FROM hybdChatRoom a LEFT JOIN hybdChatMember b on b.hycrSeq = a.hycrSeq
WHERE 1=1 AND b.hymmSeq = #{hymmSeq} AND a.hycrSeq = #{hycrSeq}
