mapper.select:
SELECT a.hymmSeq , a.hymmName , d.uuidFileName , d.path
FROM hybdMember a LEFT JOIN hybdSpaceMember b on b.hymmSeq = a.hymmSeq LEFT JOIN hybdSpace c on c.hyspSeq = b.hyspSeq LEFT JOIN hybdMemberUploaded d on d.pseq = a.hymmSeq
WHERE 1=1 AND b.hysmAcceptedNy = '1' AND b.hysmExitNy = '0' AND b.hyspSeq = #{hyspSeq}
