mapper.sql:
from hybdDocument a LEFT JOIN hybdMember b on b.hymmSeq = a.hymmSeq LEFT JOIN hybdSpace d on d.hyspSeq = a.hyspSeq where a. hydcDelNy = 0 AND a. hydcTempNy = 0 AND d.hyspSeq = #{hyspSeq} AND hydcName LIKE concat('%',#{shHydcTitle},'%')
AND hydcTitle LIKE concat('%',#{shHydcValue},'%') AND hydcText LIKE concat('%',#{shHydcValue},'%')
limit #{startRnumForMysql} , #{rowNumToShow}

mapper.update:

UPDATE hybdDocument SET
hydcTitle  =  #{hydcTitle}
,hydcText =  #{hydcText}
,hydcTempNy =  0

,modIp =  #{modIp}
,modSeq =  #{modSeq}
,modDevice =  #{modDevice}
,modDateTime =  now()
,modDateTimeSvr =  now()
WHERE 1 = 1
AND hydcSeq = #{hydcSeq}


UPDATE
	${tableName}
SET
	originalFileName = #{originalFileName}
	, uuidFileName = #{uuidFileName}
	, ext = #{ext}
	, size = #{size}
	, path = #{path}
WHERE 1=1
	AND pseq = #{pseq}
