mapper.select:
SELECT a.hymmName
FROM hybdMember a LEFT JOIN hybdSpaceMember b on b.hymmSeq = a.hymmSeq LEFT JOIN hybdSpace c on c.hyspSeq = b.hyspSeq
WHERE 1=1 AND b.hysmAcceptedNy = '1' AND b.hysmExitNy = '0' AND b.hyspSeq = #{hyspSeq}

SELECT a.hyspSeq , a.hyspName , b.hysmRoleCd
FROM hybdSpace a LEFT JOIN hybdSpaceMember b ON b.hyspSeq = a.hyspSeq
WHERE 1=1 AND b.hyspSeq = #{hyspSeq} AND b.hymmSeq = #{hymmSeq}

