mapper.select:
SELECT seq
, defaultNy , sort , originalFileName , uuidFileName , ext
, delNy , pseq , path FROM hybdMemberUploaded WHERE 1=1 AND pseq = #{hymmSeq}

mapper.update:

		UPDATE hybdMember SET
