mapper.select:
SELECT a.hyspName , a.hyspSeq , b.hysmSeq , c.hymmName
FROM hybdSpace a LEFT JOIN hybdSpaceMember b on b.hyspSeq = a.hyspSeq LEFT JOIN hybdMember c on c.hymmSeq = b.hymmSeq
WHERE 1=1 AND b.hyspSeq = #{hyspSeq} AND b.hymmSeq = #{hymmSeq}

mapper.update:
UPDATE hybdSpace
SET
hyspTypeCd = #{hyspTypeCd}
, hyspUseNy = #{hyspUseNy}
, hyspName = #{hyspName}
, modDateTime = #{modDateTime}
, modDateTimeSvr = #{modDateTimeSvr}

WHERE 1=1
AND hyspSeq
= #{hyspSeq}

UPDATE hybdSpaceMember
SET
hysmRoleCd = #{hysmRoleCd}
, hysmAcceptedNy = #{hysmAcceptedNy}
, hysmExitNy = #{hysmExitNy}
, hysmActiveNy = #{hysmActiveNy}

WHERE 1=1
AND hysmSeq = #{hysmSeq}

mapper.delete:
DELETE FROM hybdSpace
WHERE 1=1
AND hyspSeq = #{hyspSeq}
