mapper.insert:
INSERT INTO hybdSpaceMember (
hysmSeq,
hyspSeq,
hymmSeq,
hysmRoleCd
) VALUES (
#{hysmSeq}
, #{hyspSeq}
, #{hymmSeq}
, 13
)


	SELECT last_insert_id()

mapper.update:
UPDATE hybdSpace
SET
hyspTypeCd = #{hyspTypeCd}
, hyspUseNy = #{hyspUseNy}
, hyspName = #{hyspName}
, modDateTime = #{modDateTime}
, modDateTimeSvr = #{modDateTimeSvr}

WHERE 1=1
AND hyspSeq = #{hyspSeq}

UPDATE hybdSpaceMember
SET
hysmRoleCd = #{hysmRoleCd}
, hysmAcceptedNy = #{hysmAcceptedNy}
, hysmExitNy = #{hysmExitNy}
, hysmActiveNy = #{hysmActiveNy}

WHERE 1=1
AND hysmSeq = #{hysmSeq}

mapper.delete:
DELETE FROM hybdSpace 
WHERE 1=1 
AND hyspSeq = #{hyspSeq}
