mapper.insert:
INSERT INTO hybdSpace (
hyspSeq
, hyspOrder
, hyspTypeCd
, hyspUseNy
, hyspName
, hyspDelNy
, regIp
, regSeq
, regDevice
, regDateTime
, regDateTimeSvr
, modIp
, modSeq
, modDevice
, modDateTime
, modDateTimeSvr
) VALUES (
#{hyspSeq}
, #{hyspOrder}
, #{hyspTypeCd}
, #{hyspUseNy}
, #{hyspName}
, 0
, #{regIp}
, #{regSeq}
, #{regDevice}
, now()
, now()
, #{modIp}
, #{modSeq}
, #{modDevice}
, now()
, now()
)

	SELECT last_insert_id()

mapper.update:
UPDATE hybdSpace
SET
hyspTypeCd = #{hyspTypeCd}
, hyspUseNy = #{hyspUseNy}
, hyspName = #{hyspName}
, modIp = #{modIp}
, modSeq = #{modSeq}
, modDevice = #{modDevice}
, modDateTime = #{modDateTime}
, modDateTimeSvr = #{modDateTimeSvr}

WHERE 1=1
AND hyspSeq = #{hyspSeq}

mapper.delete:
DELETE FROM hybdSpace 
WHERE 1=1 
AND hyspSeq = #{hyspSeq}
