mapper.insert:
INSERT INTO hybdPlan (
hyplName
,hyplDefaultNy
,hyplDelNy
,hyplDate
,hyplDesc
,hyplMemberName
,hyspSeq
)
VALUES (
#{hyplName}
,1
,0
,#{hyplDate}
,#{hyplDesc}
,#{hyplMemberName}
,#{hyspSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE
	hybdPlan  
SET
hyplName = #{hyplName}
,hyplDate = #{hyplDate}
,hyplDesc = #{hyplDesc} 
,hyplMemberName = #{hyplMemberName} 
WHERE
	1 = 1
AND hyspSeq = #{hyspSeq}
AND hyplSeq = #{hyplSeq}
